import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Sparkles } from 'lucide-react';
import Snowflake from '../components/common/Snowflake';
import ChristmasLights from '../components/common/ChristmasLights';
import { evaluatorLogin } from '../services/authService';

const HomePage = () => {
  const navigate = useNavigate();
  const [showEvaluatorLogin, setShowEvaluatorLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleEvaluatorLogin = async () => {
    if (!username || !password) {
      setError('Please enter username and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      await evaluatorLogin(username, password);
      navigate('/evaluator');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  if (showEvaluatorLogin) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-red-900 via-green-900 to-red-900 relative overflow-hidden">
        {/* Christmas Lights Header */}
        <div className="absolute top-0 left-0 right-0 h-3 z-20" style={{
          background: 'repeating-linear-gradient(90deg, #16a34a 0px, #16a34a 30px, #fbbf24 30px, #fbbf24 60px, #dc2626 60px, #dc2626 90px)',
          animation: 'lights 2s linear infinite'
        }}></div>

        {/* Header Section - Exact Match */}
        <div className="relative z-10 pt-3" style={{
          background: 'linear-gradient(to right, #991b1b, #92400e, #166534)',
          padding: '30px 20px',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)'
        }}>
          <h1 className="text-center text-white font-bold drop-shadow-2xl" style={{
            fontFamily: "'Mountains of Christmas', cursive",
            fontSize: 'clamp(2rem, 5vw, 3.5rem)',
            textShadow: '0 0 10px #ffd700, 0 0 20px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5)',
            animation: 'glow 2s ease-in-out infinite',
            margin: '10px 0'
          }}>
            üéÑ Christmas Crib Competition 2025 üéÑ
          </h1>
          <h2 className="text-center text-white text-xl mt-2" style={{ opacity: 0.9 }}>
            Evaluation Portal
          </h2>
        </div>
        
        {/* Background Image with Overlay */}
        <div 
          className="absolute inset-0 bg-cover bg-center opacity-40"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1543589077-47d81606c1bf?w=1920&q=80')",
            backgroundBlendMode: 'overlay',
            top: '120px'
          }}
        />

        {[...Array(30)].map((_, i) => (
          <Snowflake
            key={i}
            delay={i * 0.3}
            duration={10 + Math.random() * 10}
            left={Math.random() * 100}
          />
        ))}

        <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
          <div className="bg-white/98 backdrop-blur-lg rounded-2xl shadow-2xl p-8 max-w-md w-full transform animate-scale-in border-4 border-red-200" style={{ marginTop: '-50px' }}>
            <div className="text-center mb-6">
              <div className="flex justify-center gap-5 text-4xl mb-4">
                <span>üéÅ</span>
                <span>üéÑ</span>
                <span>‚≠ê</span>
              </div>
              <h2 className="text-3xl font-bold text-red-700 animate-fade-in">
                Welcome, Evaluator!
              </h2>
              <p className="text-gray-600 mt-3">Let's evaluate the Christmas cribs! üè†</p>
            </div>

            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 animate-shake">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div className="animate-slide-in" style={{ animationDelay: '0.1s' }}>
                <label className="block text-green-700 font-semibold mb-2 text-lg">Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full p-3 border-2 border-green-600 rounded-lg focus:border-red-600 focus:outline-none transition-all duration-300 focus:shadow-lg"
                  placeholder="Enter your username"
                />
              </div>

              <div className="animate-slide-in" style={{ animationDelay: '0.2s' }}>
                <label className="block text-green-700 font-semibold mb-2 text-lg">Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full p-3 border-2 border-green-600 rounded-lg focus:border-red-600 focus:outline-none transition-all duration-300 focus:shadow-lg"
                  placeholder="Enter your password"
                />
              </div>

              <button
                onClick={handleEvaluatorLogin}
                disabled={loading}
                className="w-full text-white py-3 rounded-lg text-lg font-bold shadow-lg transform hover:scale-105 hover:-translate-y-1 transition-all disabled:opacity-50 disabled:cursor-not-allowed animate-slide-in border-2 border-red-400"
                style={{ 
                  animationDelay: '0.3s',
                  background: 'linear-gradient(135deg, #dc2626, #991b1b)'
                }}
              >
                {loading ? 'Logging in...' : 'Login'}
              </button>

              <button
                onClick={() => setShowEvaluatorLogin(false)}
                className="w-full bg-gray-500 hover:bg-gray-600 text-white py-2 rounded-lg font-semibold transform hover:scale-105 transition-all animate-slide-in"
                style={{ animationDelay: '0.4s' }}
              >
                Back
              </button>
            </div>
          </div>
        </div>

        <style jsx>{`
          @keyframes lights {
            to {
              background-position: 90px 0;
            }
          }

          @keyframes glow {
            0%, 100% {
              text-shadow: 0 0 10px #ffd700, 0 0 20px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5);
            }
            50% {
              text-shadow: 0 0 20px #fff, 0 0 30px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5);
            }
          }

          @keyframes scale-in {
            from {
              opacity: 0;
              transform: scale(0.8);
            }
            to {
              opacity: 1;
              transform: scale(1);
            }
          }

          @keyframes slide-in {
            from {
              opacity: 0;
              transform: translateX(-20px);
            }
            to {
              opacity: 1;
              transform: translateX(0);
            }
          }

          @keyframes shake {
            0%, 100% { transform: translateX(0); }
            25% { transform: translateX(-10px); }
            75% { transform: translateX(10px); }
          }

          @keyframes fade-in {
            from {
              opacity: 0;
              transform: translateY(-10px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .animate-scale-in {
            animation: scale-in 0.5s ease-out;
          }

          .animate-slide-in {
            animation: slide-in 0.5s ease-out both;
          }

          .animate-shake {
            animation: shake 0.5s ease-in-out;
          }

          .animate-fade-in {
            animation: fade-in 0.8s ease-out;
          }
        `}</style>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-900 via-green-900 to-red-900 relative overflow-hidden">
      {/* Christmas Lights Header */}
      <div className="absolute top-0 left-0 right-0 h-3 z-20" style={{
        background: 'repeating-linear-gradient(90deg, #16a34a 0px, #16a34a 30px, #fbbf24 30px, #fbbf24 60px, #dc2626 60px, #dc2626 90px)',
        animation: 'lights 2s linear infinite'
      }}></div>

      {/* Header Section - Exact Match */}
      <div className="relative z-10 pt-3" style={{
        background: 'linear-gradient(to right, #991b1b, #92400e, #166534)',
        padding: '30px 20px',
        boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)'
      }}>
        <h1 className="text-center text-white font-bold drop-shadow-2xl" style={{
          fontFamily: "'Mountains of Christmas', cursive",
          fontSize: 'clamp(2rem, 5vw, 3.5rem)',
          textShadow: '0 0 10px #ffd700, 0 0 20px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5)',
          animation: 'glow 2s ease-in-out infinite',
          margin: '10px 0'
        }}>
          üéÑ Christmas Crib Competition 2025 üéÑ
        </h1>
        <h2 className="text-center text-white text-xl mt-2" style={{ opacity: 0.9 }}>
          Evaluation Portal
        </h2>
      </div>
      
      {/* Background Image with Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center opacity-20"
        style={{
          backgroundImage: "url('https://images.unsplash.com/photo-1543589077-47d81606c1bf?w=1920&q=80')",
          backgroundBlendMode: 'overlay',
          top: '120px'
        }}
      />

      {[...Array(30)].map((_, i) => (
        <Snowflake
          key={i}
          delay={i * 0.3}
          duration={10 + Math.random() * 10}
          left={Math.random() * 100}
        />
      ))}

      <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
        <div className="bg-white/95 backdrop-blur-sm rounded-3xl shadow-2xl p-12 max-w-md w-full transform animate-scale-in border-4 border-purple-400">
          <div className="text-center w-full">
            <div className="mb-8 animate-bounce">
              <Sparkles className="w-20 h-20 mx-auto text-yellow-400 drop-shadow-lg" />
            </div>

            <div className="space-y-6 animate-slide-up">
              <button
                onClick={() => setShowEvaluatorLogin(true)}
                className="text-white px-10 py-5 rounded-full text-xl font-bold shadow-2xl transform hover:scale-110 hover:-translate-y-1 transition-all duration-300 w-full mx-auto block border-4 border-red-400 hover:border-red-300 hover:shadow-red-500/50"
                style={{ background: 'linear-gradient(135deg, #dc2626, #991b1b)' }}
              >
                Evaluator Login ‚≠ê
              </button>

              <button
                onClick={() => navigate('/admin-login')}
                className="text-white px-10 py-5 rounded-full text-xl font-bold shadow-2xl transform hover:scale-110 hover:-translate-y-1 transition-all duration-300 w-full mx-auto block border-4 border-green-400 hover:border-green-300 hover:shadow-green-500/50"
                style={{ background: 'linear-gradient(135deg, #16a34a, #14532d)' }}
              >
                Admin Login üèÜ
              </button>
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes lights {
          to {
            background-position: 90px 0;
          }
        }

        @keyframes glow {
          0%, 100% {
            text-shadow: 0 0 10px #ffd700, 0 0 20px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5);
          }
          50% {
            text-shadow: 0 0 20px #fff, 0 0 30px #ffd700, 3px 3px 6px rgba(0, 0, 0, 0.5);
          }
        }

        @keyframes slide-up {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes scale-in {
          from {
            opacity: 0;
            transform: scale(0.8);
          }
          to {
            opacity: 1;
            transform: scale(1);
          }
        }

        .animate-slide-up {
          animation: slide-up 1s ease-out 0.3s both;
        }

        .animate-scale-in {
          animation: scale-in 0.5s ease-out;
        }
      `}</style>
    </div>
  );
};

export default HomePage;
