// ============================================================================
// frontend/src/pages/HomePage.jsx
// ============================================================================
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Snowflake from '../components/common/Snowflake';
import ChristmasLights from '../components/common/ChristmasLights';
import { evaluatorLogin } from '../services/authService';

const HomePage = () => {
  const navigate = useNavigate();
  const [showEvaluatorLogin, setShowEvaluatorLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleEvaluatorLogin = async () => {
    if (!username || !password) {
      setError('Please enter username and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      await evaluatorLogin(username, password);
      navigate('/evaluator');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  if (showEvaluatorLogin) {
    return (
      <div className="app">
        <div className="snow-container">
          {[...Array(30)].map((_, i) => (
            <Snowflake
              key={i}
              delay={i * 0.3}
              duration={10 + Math.random() * 10}
              left={Math.random() * 100}
            />
          ))}
        </div>

        <div className="christmas-header">
          <div className="header-lights"></div>
          <h1 className="main-title">üéÑ Christmas Crib Competition 2025 üéÑ</h1>
          <p className="subtitle">Evaluation Portal</p>
        </div>

        <div className="main-content">
          <div className="home-container">
            <div className="welcome-card-small">
              <div className="icon-row">
                <span style={{ fontSize: '3rem' }} className="icon-bounce">üéÅ</span>
                <span style={{ fontSize: '3rem' }} className="icon-pulse">üéÑ</span>
                <span style={{ fontSize: '3rem' }} className="icon-bounce">‚≠ê</span>
              </div>

              <h2 className="welcome-title">Welcome, Evaluator!</h2>
              <p className="welcome-text">Let's evaluate the Christmas cribs! üè†</p>

              {error && (
                <div style={{
                  background: '#fee',
                  border: '2px solid #c41e3a',
                  color: '#c41e3a',
                  padding: '12px',
                  borderRadius: '8px',
                  marginBottom: '15px',
                  textAlign: 'center',
                  fontWeight: '600'
                }}>
                  {error}
                </div>
              )}

              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="input-field"
                  placeholder="Enter your username"
                />
              </div>

              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="input-field"
                  placeholder="Enter your password"
                  onKeyPress={(e) => e.key === 'Enter' && handleEvaluatorLogin()}
                />
              </div>

              <button
                onClick={handleEvaluatorLogin}
                disabled={loading}
                className="btn btn-primary"
              >
                {loading ? 'üéÖ Logging in...' : 'üéÖ Login'}
              </button>

              <button
                onClick={() => setShowEvaluatorLogin(false)}
                className="btn btn-secondary"
                style={{ marginTop: '10px' }}
              >
                ‚Üê Back to Home
              </button>
            </div>
          </div>
        </div>

        <div className="christmas-footer">
          <p>üéÑ Merry Christmas 2025 üéÑ</p>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <div className="snow-container">
        {[...Array(30)].map((_, i) => (
          <Snowflake
            key={i}
            delay={i * 0.3}
            duration={10 + Math.random() * 10}
            left={Math.random() * 100}
          />
        ))}
      </div>

      <div className="christmas-header">
        <div className="header-lights"></div>
        <h1 className="main-title">üéÑ Christmas Crib Competition 2025 üéÑ</h1>
        <p className="subtitle">Evaluation Portal</p>
      </div>

      <div className="main-content">
        <div className="home-container">
          <div className="welcome-card-small">
            <div className="icon-row">
              <span style={{ fontSize: '3rem' }} className="icon-bounce">üéÅ</span>
              <span style={{ fontSize: '3rem' }} className="icon-pulse">üéÑ</span>
              <span style={{ fontSize: '3rem' }} className="icon-bounce">‚≠ê</span>
            </div>

            <h2 className="welcome-title">Welcome, Evaluator!</h2>
            <p className="welcome-text">Let's evaluate the Christmas cribs! üè†</p>

            <div className="form-group">
              <label>Select Department Code</label>
              <select className="select-field">
                <option>-- Choose Department Code --</option>
                <option>CSE - Computer Science</option>
                <option>ECE - Electronics</option>
                <option>MECH - Mechanical</option>
                <option>CIVIL - Civil Engineering</option>
                <option>EEE - Electrical</option>
              </select>
            </div>

            <button
              onClick={() => setShowEvaluatorLogin(true)}
              className="btn btn-primary"
            >
              üéÑ Start Evaluation
            </button>

            <button
              onClick={() => navigate('/results')}
              className="btn btn-secondary"
              style={{ marginTop: '10px' }}
            >
              üìä View Results
            </button>
          </div>
        </div>
      </div>

      <div className="christmas-footer">
        <p>üéÑ Merry Christmas 2025 üéÑ</p>
      </div>
    </div>
  );
};

export default HomePage;
