export const CRITERIA = [
  { name: 'socialRelevance', label: 'Social Relevance Theme', max: 15 },
  { name: 'creativity', label: 'Creativity', max: 15 },
  { name: 'naturalElements', label: 'Use of Natural Elements', max: 10 },
  { name: 'nonVerbal', label: 'Non-Verbal Presentation', max: 10 }
];

export const GUIDELINES = [
  'Central theme must be social issues',
  'One staff & one student must be present during evaluation',
  'No quotes/placards allowed',
  'No plastic or thermocol',
  'No verbal explanation allowed'
];

export const MAX_TOTAL_MARKS = 50;
