import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Sparkles } from 'lucide-react';
import Snowflake from '../components/common/Snowflake';
import ChristmasLights from '../components/common/ChristmasLights';
import { evaluatorLogin } from '../services/authService';

const HomePage = () => {
  const navigate = useNavigate();
  const [showEvaluatorLogin, setShowEvaluatorLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleEvaluatorLogin = async () => {
    if (!username || !password) {
      setError('Please enter username and password');
      return;
    }

    setLoading(true);
    setError('');

    try {
      await evaluatorLogin(username, password);
      navigate('/evaluator');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  if (showEvaluatorLogin) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-red-900 via-green-900 to-red-900 relative overflow-hidden">
        {/* Background Image with Overlay */}
        <div 
          className="absolute inset-0 bg-cover bg-center opacity-30"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1512389142860-9c449e58a543?w=1920&q=80')",
            backgroundBlendMode: 'overlay'
          }}
        />
        
        <ChristmasLights />
        {[...Array(30)].map((_, i) => (
          <Snowflake
            key={i}
            delay={i * 0.3}
            duration={10 + Math.random() * 10}
            left={Math.random() * 100}
          />
        ))}

        <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
          <div className="bg-white/95 backdrop-blur-lg rounded-2xl shadow-2xl p-8 max-w-md w-full transform animate-scale-in border-4 border-red-200">
            <h2 className="text-3xl font-bold text-red-700 text-center mb-6 animate-fade-in">
              üéÑ Evaluator Login üéÑ
            </h2>

            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4 animate-shake">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div className="animate-slide-in" style={{ animationDelay: '0.1s' }}>
                <label className="block text-gray-700 font-semibold mb-2">Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full p-3 border-2 border-gray-300 rounded-lg focus:border-red-600 focus:outline-none transition-all duration-300 focus:shadow-lg"
                  placeholder="Enter your username"
                />
              </div>

              <div className="animate-slide-in" style={{ animationDelay: '0.2s' }}>
                <label className="block text-gray-700 font-semibold mb-2">Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full p-3 border-2 border-gray-300 rounded-lg focus:border-red-600 focus:outline-none transition-all duration-300 focus:shadow-lg"
                  placeholder="Enter your password"
                />
              </div>

              <button
                onClick={handleEvaluatorLogin}
                disabled={loading}
                className="w-full bg-red-600 hover:bg-red-700 text-white py-3 rounded-lg text-lg font-bold shadow-lg transform hover:scale-105 hover:-translate-y-1 transition-all disabled:opacity-50 disabled:cursor-not-allowed animate-slide-in border-2 border-red-400"
                style={{ animationDelay: '0.3s' }}
              >
                {loading ? 'Logging in...' : 'Login'}
              </button>

              <button
                onClick={() => setShowEvaluatorLogin(false)}
                className="w-full bg-gray-500 hover:bg-gray-600 text-white py-2 rounded-lg font-semibold transform hover:scale-105 transition-all animate-slide-in"
                style={{ animationDelay: '0.4s' }}
              >
                Back
              </button>
            </div>

            <style jsx>{`
              @keyframes scale-in {
                from {
                  opacity: 0;
                  transform: scale(0.8);
                }
                to {
                  opacity: 1;
                  transform: scale(1);
                }
              }

              @keyframes slide-in {
                from {
                  opacity: 0;
                  transform: translateX(-20px);
                }
                to {
                  opacity: 1;
                  transform: translateX(0);
                }
              }

              @keyframes shake {
                0%, 100% { transform: translateX(0); }
                25% { transform: translateX(-10px); }
                75% { transform: translateX(10px); }
              }

              .animate-scale-in {
                animation: scale-in 0.5s ease-out;
              }

              .animate-slide-in {
                animation: slide-in 0.5s ease-out both;
              }

              .animate-shake {
                animation: shake 0.5s ease-in-out;
              }
            `}</style>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-900 via-green-900 to-red-900 relative overflow-hidden">
      {/* Background Image with Overlay */}
      <div 
        className="absolute inset-0 bg-cover bg-center opacity-30"
        style={{
          backgroundImage: "url('https://images.unsplash.com/photo-1512389142860-9c449e58a543?w=1920&q=80')",
          backgroundBlendMode: 'overlay'
        }}
      />
      
      <ChristmasLights />
      {[...Array(30)].map((_, i) => (
        <Snowflake
          key={i}
          delay={i * 0.3}
          duration={10 + Math.random() * 10}
          left={Math.random() * 100}
        />
      ))}

      <div className="relative z-10 min-h-screen flex items-center justify-center p-4">
        <div className="text-center">

          <div className="mb-8 animate-bounce">
            <Sparkles className="w-20 h-20 mx-auto text-yellow-300 drop-shadow-lg" />
          </div>

          <h1 className="text-5xl md:text-7xl font-bold text-white mb-4 animate-pulse drop-shadow-2xl">
            üéÑ Crib Competition üéÑ
          </h1>

          <h2 className="text-2xl md:text-3xl text-green-300 mb-12 font-semibold drop-shadow-lg animate-fade-in">
            Evaluation System 2025
          </h2>

          <div className="space-y-6 animate-slide-up">
            <button
              onClick={() => setShowEvaluatorLogin(true)}
              className="bg-red-600 hover:bg-red-700 text-white px-10 py-5 rounded-full text-xl font-bold shadow-2xl transform hover:scale-110 hover:-translate-y-1 transition-all duration-300 w-72 mx-auto block border-4 border-red-400 hover:border-red-300 hover:shadow-red-500/50"
            >
              Evaluator Login ‚≠ê
            </button>

            <button
              onClick={() => navigate('/admin-login')}
              className="bg-green-600 hover:bg-green-700 text-white px-10 py-5 rounded-full text-xl font-bold shadow-2xl transform hover:scale-110 hover:-translate-y-1 transition-all duration-300 w-72 mx-auto block border-4 border-green-400 hover:border-green-300 hover:shadow-green-500/50"
            >
              Admin Login üèÜ
            </button>
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(-20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slide-up {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .animate-fade-in {
          animation: fade-in 1s ease-out;
        }

        .animate-slide-up {
          animation: slide-up 1s ease-out 0.3s both;
        }
      `}</style>
    </div>
    
  );
};

export default HomePage;
