import React, { useState } from 'react';
import { Gift, TreePine, Star } from 'lucide-react';

const departments = [
  { code: '1', name: 'Civil' },
  { code: '2', name: 'CSE' },
  { code: '3', name: 'EEE' },
  { code: '4', name: 'ECE' },
  { code: '5', name: 'IT' },
  { code: '6', name: 'MECH' },
  { code: '7', name: 'AI&DS AND AIML' },
  { code: '8', name: 'CSBS' },
  { code: '9', name: 'MBA' },
  { code: '10', name: 'MCA' },
];

function Home({ setCurrentView, setSelectedDept }) {
  const [deptCode, setDeptCode] = useState('');

  const handleStart = () => {
    if (!deptCode) {
      alert('Please select a department code!');
      return;
    }
    const dept = departments.find(d => d.code === deptCode);
    setSelectedDept(dept);
    setCurrentView('guidelines');
  };

  return (
    <div className="home-container">
      <div className="welcome-card-small">
        <div className="icon-row">
          <Gift className="icon-bounce" size={35} color="#c41e3a" />
          <TreePine className="icon-spin" size={45} color="#0f7d3a" />
          <Star className="icon-pulse" size={35} color="#ffd700" />
        </div>

        <h2 className="welcome-title">Welcome, Evaluator!</h2>
        <p className="welcome-text">
          Let's evaluate the Christmas cribs! ðŸŽ…
        </p>

        <div className="form-group">
          <label>Select Department Code</label>
          <select
            value={deptCode}
            onChange={(e) => setDeptCode(e.target.value)}
            className="select-field"
          >
            <option value="">-- Choose Department Code --</option>
            {departments.map(dept => (
              <option key={dept.code} value={dept.code}>
                {dept.code}
              </option>
            ))}
          </select>
        </div>

        <button onClick={handleStart} className="btn btn-primary">
          Start Evaluation ðŸŽ„
        </button>

        <button 
          onClick={() => setCurrentView('results')} 
          className="btn btn-secondary"
        >
          View Results ðŸ“Š
        </button>
      </div>
    </div>
  );
}

export default Home;
